import React, { useState, useEffect } from "react";
import Start from "./Components/Start";
import Questions from "./Components/Questions";
import Answers from "./Components/Answers";

function App() {

  // const [startQuiz, setStartQuiz] = useState(false)
  // const [triviaData, setTriviaData] = useState([])
  const [appState, setAppState] = useState({
    data:null,
    start: false,
    loading: true
  })
  const {data, start, loading} = appState

  function quizStart(){
    setAppState(prevState=>(
      {...prevState, start:true}
    ))
    getQuestions()
  }

  function getQuestions(){
    fetch('https://opentdb.com/api.php?amount=5&difficulty=medium&type=multiple')
    .then(res=> res.json())
    .then(data=> setAppState(prevState=>(
      {...prevState, loading:false, data:data.results}
    )))
  }

  if(appState.loading == false) console.log(appState.data)

  // useEffect(()=>{
  //   if(!loading){

  //   }
  // }, [appState])

  return (
    <div className="App">
      {start ? <Questions loading={loading} /> : <Start start={quizStart} />}
      {(start && !loading)  && <Answers />}
    </div>
  );

}

export default App;
