import React, { useContext } from 'react'
import Question from './Question'
import {Context} from "./Context"

export default function Questions(props) {
  const {arrQuestions, checkAnswers, playAgain, correctAnswers, reset,score} = useContext(Context)
  const questions = arrQuestions.map(item => (
    <Question 
      key={item.question}
      question={item.question} 
      correctAnswer={item.correct_answer} 
      incorrectAnswers={item.incorrect_answers}
    />
  ))
  // console.log(questions)
  return (
    <div>
      <h1>Questions</h1>
      {questions}
      {playAgain && 
      <>
        <h4>You got {correctAnswers} questions right</h4>
        <button onClick={reset}>Play again</button>
      </>} 
      {arrQuestions === [] && <button onClick={checkAnswers}>Check answers</button>}
      <h4>Score: {score}</h4>
    </div>
  )
}
