import React, {useState, useEffect, useContext} from 'react'
import Answer from './Answer'
import { Context } from "./Context"

function Question({question, incorrectAnswers, correctAnswer}) {
  const arrAnswers = incorrectAnswers.concat(correctAnswer)
  const [answers, setAnswers] = useState()
  const {startQuiz, playAgain} = useContext(Context)
  function shuffleArr(arr){

    let currentIndex = arr.length, randomIndex

    while(currentIndex !== 0){

      randomIndex = Math.floor(Math.random() * currentIndex)

      currentIndex--

      [arr[currentIndex], arr[randomIndex]] = [
        arr[randomIndex], arr[currentIndex]]

    }

    return arr

  }

  const shuffledAnswers = shuffleArr(arrAnswers)

  useEffect(() => {
    if(!playAgain && startQuiz){
      setAnswers(() => {
        shuffledAnswers.map((item) => {
          return <Answer key={item} answers={item} correctanswer={correctAnswer}/>
        })
      })
    }
    console.log(answers)
  }, [startQuiz, playAgain])


  return (
    <div className='question-container'>
        <h3 className='question'>{atob(question)}</h3>
        {answers}
        <hr className='hr'/>
    </div>
  )
}

export default Question
