import React, { useState, useEffect, useContext } from 'react'
import { Context } from "./Context"

export default function Answer(props) {
  const {
    setCorrectAnswers, 
    playAgain, 
    selectedAnswers, 
    setSelectedAnswers
  } = useContext(Context)
  // const [selectedAnswer, setSelectedAnswer] = useState(null)
  const [correctAnswer, setCorrectAnswer] = useState(props.correctanswer)
  
  function classButtonTest(){
    selectedAnswers.forEach(element => {
      if(element.selectedAnswer === props.answers){
        return true
      } else{
        return false
      }
    });
  }
  const selectedAnswerClass = classButtonTest() ? "selected-answer" : "answer-button"
  
  function selectAnswer(){
    setSelectedAnswers(prevState => prevState.map(item => {
      return correctAnswer === item.id ? {...item, selectedAnswer: props.answers} : {...item}
    }))
  }

  // useEffect(() => {
  //   if(selectedAnswer === correctAnswer){
  //     setCorrectAnswers(prevState => prevState + 1)
  //   }
  // }, [playAgain])
  
  
  // console.log(selectedAnswer)
  // console.log(props.answers)
  return (
        <button 
        className={selectedAnswerClass} 
        onClick={selectAnswer}>{atob(props.answers)}</button>
  )
}
// Make state with object that includes both correct and selected answers
